CREATE TABLE IF NOT EXISTS instituicao (id INTEGER AUTO_INCREMENT PRIMARY KEY, nome VARCHAR(255) NOT NULL, tipo VARCHAR(50) NOT NULL);
CREATE TABLE IF NOT EXISTS evento (id INTEGER AUTO_INCREMENT PRIMARY KEY, nome VARCHAR(255) NOT NULL, data_inicial DATE NOT NULL, data_final DATE NOT NULL, ativo BOOLEAN NOT NULL DEFAULT FALSE, instituicao_id INTEGER NOT NULL, FOREIGN KEY (instituicao_id) REFERENCES instituicao(id));
